mapper.select:
select v_id, v_title, v_pStatus, v_pBgnD, v_pEndD, v_mBgnD, v_mEndD, r_id, ca_id, v_Auth, v_x, v_y from `vol` where v_pStatus between 1 and 3
and v_pBgnD >= #{v_pBgnD} and v_pEndD <= #{v_pEndD}
and v_bgnTm >= #{v_bgnTm} and v_endTm <= #{v_endTm}
and r_id in (select r_id from region where r_sidoNm = #{r_sidoNm} and r_gugunNm = #{r_gugunNm})
and ( v_location like CONCAT('%',#{vol_title},'%') or v_title like CONCAT('%',#{vol_title},'%') or v_organ like CONCAT('%',#{vol_title},'%') or v_detail like CONCAT('%',#{vol_title},'%') )
and ca_id in (select ca_id from category where ca_highNm = #{ca_highNm})
and v_id is not null
limit #{listSize} offset #{startList}

update vol set
v_bgnTm = #{v_bgnTm},
v_endTm = #{v_endTm},
v_appnow = #{v_appnow}, 
v_target = #{v_target}
where v_id = #{v_id}
