import React from "react";

class Join extends React.Component {
  state = {
    email: "",
    password: "",
    passwordConfirm: "",
    nickName: "",
    isTerm: false,
    isLoading: false,
    error: {
      email: false,
      password: false,
      nickName: false,
      passwordConfirm: false,
      term: false
    },
    isSubmit: false,
    passwordType: "password",
    passwordConfirmType: "password",
    termPopup: false
  };

  render() {
    return (
      <div>
        <h1>가입하기</h1>
        <div className="wrapC">
          <div>
            <input
              v-model="nickName"
              id="nickname"
              placeholder="닉네임을 입력하세요."
              type="text"
            />
            <label htmlFor="nickname">닉네임</label>
          </div>

          <div className="input-with-label">
            <input
              value={this.state.email}
              v-bind="{error : error.password, complete:!error.password&&password.length!==0}"
              onKeyDown={event => {
                if (event.key === "Enter") {
                  this.login();
                }
              }}
              onChange={this.handleInput}
              id="email"
              placeholder="이메일을 입력하세요."
              type="text"
            />
            <label htmlFor="email">이메일</label>
            <div className="error-text" v-if="error.email">
              {this.state.error.email}
            </div>
          </div>

          <div className="input-with-label">
            <input
              value={this.state.password}
              type="password"
              v-bind="{error : error.password, complete:!error.password&&password.length!==0}"
              id="password"
              onKeyDown={event => {
                if (event.key === "Enter") {
                  this.login();
                }
              }}
              onChange={this.handleInput2}
              placeholder="비밀번호를 입력하세요."
            />
            <label htmlFor="password">비밀번호</label>
            <div className="error-text" v-if="error.password">
              {this.state.error.password}
            </div>
          </div>

          <div>
            <input
              v-model="passwordConfirm"
              type="passwordConfirmType"
              id="password-confirm"
              placeholder="비밀번호를 다시한번 입력하세요."
            />
            <label htmlFor="password-confirm">비밀번호 확인</label>
          </div>
        </div>

        <label>
          <input v-model="isTerm" type="checkbox" id="term" />
          <span>약관을 동의합니다.</span>
        </label>

        <span onClick={() => this.setState({ termPopup: true })}>약관보기</span>

        <button>가입하기</button>
      </div>
    );
  }
}

export default Join;
