import React from "react";
import { Link } from "react-router-dom";
import "assets/css/style.scss";
import "assets/css/user.scss";
import * as EmailValidator from "email-validator";
import UserApi from "apis/UserApi";

class FindPassword extends React.Component {
  state = {
    email: "",
    error: {
      state: false,
      email: ""
    },
    isSubmit: false,
    component: this
  };
  componentDidMount() {}
  checkForm = () => {
    let error = { ...this.state.error };
    if (
      this.state.email.length >= 0 &&
      !EmailValidator.validate(this.state.email)
    ) {
      error.email = "이메일 형식이 아닙니다.";
    } else {
      error.state = false;
    }
    this.setState({ error: error }, () => {
      let isSubmit = true;
      Object.values(this.state.error).map(v => {
        if (v) isSubmit = false;
        return v;
      });
      this.setState({
        isSubmit: isSubmit
      });
    });
  };
  // login() {
  //   if (this.state.isSubmit) {
  //     let { email, password } = this.state;
  //     let data = {
  //       email,
  //       password
  //     };

  //     //요청 후에는 버튼 비활성화
  //     this.isSubmit = false;

  //     UserApi.requestLogin(
  //       data,
  //       res => {
  //         //통신을 통해 전달받은 값 콘솔에 출력
  //         //                        console.log(res);

  //         //요청이 끝나면 버튼 활성화
  //         this.isSubmit = true;
  //       },
  //       error => {
  //         //요청이 끝나면 버튼 활성화
  //         this.isSubmit = true;
  //       }
  //     );
  //   }
  // }
  handleInput = (e: React.FormEvent<HTMLInputElement>) => {
    this.setState({ email: e.currentTarget.value }, () => {
      this.checkForm();
    });
  };
  render() {
    return (
      <div className="user" id="login">
        <div className="wrapC">
          <h1 className="title">비밀번호 찾기</h1>
          <div className="input-with-label">
            <input
              value={this.state.email}
              v-bind="{error : error.password, complete:!error.password&&password.length!==0}"
              onKeyDown={event => {
                if (event.key === "Enter") {
                  // this.login();
                }
              }}
              onChange={this.handleInput}
              id="email"
              placeholder="이메일을 입력하세요."
              type="text"
            />
            <label htmlFor="email">이메일</label>
            <div className="error-text" v-if="error.email">
              {this.state.error.email}
            </div>
          </div>
          <Link
            to={{
              pathname: "/findpasswordmailsend",
              state: { email: this.state.email }
            }}
            className="btn--back"
          >
            <button
              className="btn btn--back btn--join"
              disabled={!this.state.isSubmit}
            >
              비밀번호 찾기 메일 전송
            </button>
          </Link>
        </div>
      </div>
    );
  }
}

export default FindPassword;
